// import React from 'react';
import React, { useEffect, useState } from "react";
import "./styles/App.css";
import twitterLogo from "./assets/twitter-logo.svg"
import {ethers} from "ethers";
import contractAbi from "./utils/contractABI.json";
import polygonLogo from './assets/polygonlogo.png';
import ethLogo from './assets/ethlogo.png';
import { networks } from './utils/networks';

// Constants
const TWITTER_HANDLE = '_UNCHAIN';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const tld = ".APT";
//"YOUR_CONTRACT_ADDRESS_HERE"
const CONTRACT_ADDRESS = "0x57BD9C3BE3e28d8084B7e462e69a611be117837E";

const App = () => {
  //„É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Ç¢„Éâ„É¨„Çπ„ÇístateÁÆ°ÁêÜ„Åó„Å¶„ÅÑ„Åæ„Åô„ÄÇÂÜíÈ†≠„ÅÆuseState„ÅÆ„Ç§„É≥„Éù„Éº„Éà„ÇíÂøò„Çå„Å™„ÅÑ„Åß„Åè„Å†„Åï„ÅÑ„ÄÇ
  const [currentAccount, setCurrentAccount] = useState("");
  // stateÁÆ°ÁêÜ„Åô„Çã„Éó„É≠„Éë„ÉÜ„Ç£„ÇíËøΩÂä†„Åó„Å¶„ÅÑ„Åæ„Åô„ÄÇ
  const [domain, setDomain] = useState("");
  const [record, setRecord] = useState("");
  // network „ÇíÁä∂ÊÖãÂ§âÊï∞„Å®„Åó„Å¶Ë®≠ÂÆö„Åó„Åæ„Åô„ÄÇ
  const [network, setNetwork] = useState("");
  // Êñ∞„Åó„ÅÑÁä∂ÊÖãÂ§âÊï∞„ÇíÂÆöÁæ©„Åó„Å¶„ÅÑ„Åæ„Åô„ÄÇ„Åì„Çå„Åæ„Åß„ÅÆ„ÇÇ„ÅÆ„ÅÆ‰∏ã„Å´ËøΩÂä†„Åó„Åæ„Åó„Çá„ÅÜ„ÄÇ
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(false);
  // Áä∂ÊÖã„ÇíÁÆ°ÁêÜ„Åô„Çã mints „ÇíÂÆöÁæ©„Åó„Åæ„Åô„ÄÇÂàùÊúüÁä∂ÊÖã„ÅØÁ©∫„ÅÆÈÖçÂàó„Åß„Åô„ÄÇ
  const [mints, setMints] = useState([]);

  // connectWallet Èñ¢Êï∞„ÇíÂÆöÁæ©
  const connectWallet = async () => {
	try {
		const { ethereum } = window;

		if (!ethereum) {
			alert("Get Metamask -> https://metamask.io/");
			return;
		}

		// „Ç¢„Ç´„Ç¶„É≥„Éà„Å∏„ÅÆ„Ç¢„ÇØ„Çª„Çπ„ÇíË¶ÅÊ±Ç„Åô„Çã„É°„ÇΩ„ÉÉ„Éâ„Çí‰ΩøÁî®„Åó„Åæ„Åô„ÄÇ
		const accounts = await ethereum.request({
			method: "eth_requestAccounts"
		});

		// Metamask „Çí‰∏ÄÂ∫¶Ë™çË®º„Åô„Çå„Å∞ Connected „Å®„Ç≥„É≥„ÇΩ„Éº„É´„Å´Ë°®Á§∫„Åï„Çå„Åæ„Åô„ÄÇ
		console.log("Connected", accounts[0]);
		setCurrentAccount(accounts[0]);
	} catch (error) {
	  console.log(error);
	}
  };

  const switchNetwork = async () => {
	if (window.ethereum) {
		try {
			// Mumbai testnet „Å´Âàá„ÇäÊõø„Åà„Åæ„Åô„ÄÇ
			await window.ethereum.request({
				method: 'wallet_switchEthereumChain',
				params: [{ chainId: '0x13881' }],// utils„Éï„Ç©„É´„ÉÄÂÜÖ„ÅÆnetworks.js „ÇíÁ¢∫Ë™ç„Åó„Åæ„Åó„Çá„ÅÜ„ÄÇ0x„ÅØ16ÈÄ≤Êï∞„Åß„Åô„ÄÇ
			});
		} catch (error) {
			// „Åì„ÅÆ„Ç®„É©„Éº„Ç≥„Éº„Éâ„ÅØÂΩìË©≤„ÉÅ„Çß„Éº„É≥„Åå„É°„Çø„Éû„Çπ„ÇØ„Å´ËøΩÂä†„Åï„Çå„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„Åß„Åô„ÄÇ
      		// „Åù„ÅÆÂ†¥Âêà„ÄÅ„É¶„Éº„Ç∂„Éº„Å´ËøΩÂä†„Åô„Çã„Çà„ÅÜ‰øÉ„Åó„Åæ„Åô„ÄÇ
			if (error.code === 4902) {
				try {
					await window.ethereum.request({
						method: 'wallet_addEthereumChain',
						params: [
							{
								chainId: '0x13881',
								chainName: 'Polygon Mumbai Testnet',
								rpcUrls: ['https://rpc-mumbai.maticvigil.com/'],
								nativeCurrency: {
									name: "Mumbai Matic",
									symbol: "MATIC",
									decimals: 18
								},
								blockExplorerUrls: ["https://mumbai.polygonscan.com/"]
							},
						],
					});
				} catch (error) {
					console.log(error);
				}
			}
			console.log(error);
		}
	} else {
		// window.ethereum „ÅåË¶ã„Å§„Åã„Çâ„Å™„ÅÑÂ†¥Âêà„É°„Çø„Éû„Çπ„ÇØ„ÅÆ„Ç§„É≥„Çπ„Éà„Éº„É´„Çí‰øÉ„Åó„Åæ„Åô„ÄÇ
		alert('Metamask is not installed. Please install it to use this app: https://metamask.io/download.html');
	}
  };


  // „Ç¶„Ç©„É¨„ÉÉ„Éà„ÅÆÊé•Á∂ö„ÇíÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
  //const checkIfWalletIsConnected = () => {
  const checkIfWalletIsConnected = async () => {
	 // window.ethereum„ÅÆË®≠ÂÆö„ÄÇ„Åì„ÅÆË°®Ë®òÊ≥ï„ÅØJavascript„ÅÆ„ÄåÂàÜÂâ≤‰ª£ÂÖ•„Äç„ÇíÂèÇÁÖß„ÄÇ
	 const {ethereum} = window;

	 if (!ethereum) {
		console.log("Make sure you have Metamask!");
		return;
	 } else {
		console.log("You have the ethereum object", ethereum);
	 }

	 // „É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Çí„É™„ÇØ„Ç®„Çπ„Éà„Åó„Åæ„Åô„ÄÇ
	 const accounts = await ethereum.request({ method: "eth_accounts" });

	 // „É¶„Éº„Ç∂„Éº„ÅåË§áÊï∞„ÅÆ„Ç¢„Ç´„Ç¶„É≥„Éà„ÇíÊåÅ„Å£„Å¶„ÅÑ„ÇãÂ†¥Âêà„ÇÇ„ÅÇ„Çä„Åæ„Åô„ÄÇ„Åì„Åì„Åß„ÅØÊúÄÂàù„ÅÆ„Ç¢„Éâ„É¨„Çπ„Çí‰Ωø„ÅÑ„Åæ„Åô„ÄÇ
	 if (accounts.length !==0) {
		const account = accounts[0];
		console.log("Found an authorized account:", account);
		setCurrentAccount(account);
	 } else {
		console.log("No authorized account found");
	 }

	 // „É¶„Éº„Ç∂„Éº„ÅÆ„Éç„ÉÉ„Éà„ÉØ„Éº„ÇØ„ÅÆ„ÉÅ„Çß„Éº„É≥ID„Çí„ÉÅ„Çß„ÉÉ„ÇØ„Åó„Åæ„Åô„ÄÇ
	const chainId = await ethereum.request({ method: 'eth_chainId' });
	setNetwork(networks[chainId]);

	ethereum.on('chainChanged', handleChainChanged);

	// „Éç„ÉÉ„Éà„ÉØ„Éº„ÇØ„ÅåÂ§â„Çè„Å£„Åü„Çâ„É™„É≠„Éº„Éâ„Åó„Åæ„Åô„ÄÇ
	function handleChainChanged(_chainId) {
		window.location.reload();
	}
  };

const mintDomain = async () => {
	// „Éâ„É°„Ç§„É≥„Åånull„ÅÆ„Å®„Åçrun„Åó„Åæ„Åõ„Çì„ÄÇ
	if (!domain) {return}
	// „Éâ„É°„Ç§„É≥„Åå3ÊñáÂ≠ó„Å´Ê∫Ä„Åü„Å™„ÅÑ„ÄÅÁü≠„Åô„Åé„ÇãÂ†¥Âêà„Å´„Ç¢„É©„Éº„Éà„ÇíÂá∫„Åó„Åæ„Åô„ÄÇ
	if (domain.length < 3) {
		alert('Domain must be at least 3 characters long');
		return;
	}
	// „Éâ„É°„Ç§„É≥„ÅÆÊñáÂ≠óÊï∞„Å´Âøú„Åò„Å¶‰æ°Ê†º„ÇíË®àÁÆó„Åó„Åæ„Åô„ÄÇ
  	// 3 chars = 0.05 MATIC, 4 chars = 0.03 MATIC, 5 or more = 0.01 MATIC
	const price = 
		// domain.length === 3 ? "0.05" : domain.length === 4 ? "0.03" : "0.01";
		domain.length === 3 ? '0.005' : domain.length === 4 ? '0.003' : '0.001';
	console.log("Minting domain", domain, "with price", price);
	try {
		const {ethereum} = window;
		if (ethereum) {
			const provider = new ethers.providers.Web3Provider(ethereum);
			const signer = provider.getSigner();
			const contract = new ethers.Contract(
				CONTRACT_ADDRESS,
				contractAbi.abi,
				signer
			);

			console.log("Going to pop wallet now to pay gas...");
			  let tx = await contract.register(domain, {
				value: ethers.utils.parseEther(price)});
			// „Éü„É≥„Éà„Åï„Çå„Çã„Åæ„Åß„Éà„É©„É≥„Ç∂„ÇØ„Ç∑„Éß„É≥„ÇíÂæÖ„Å°„Åæ„Åô„ÄÇ
			const receipt = await tx.wait();

			// „Éà„É©„É≥„Ç∂„ÇØ„Ç∑„Éß„É≥„ÅåÂïèÈ°å„Å™„ÅèÂÆüË°å„Åï„Çå„Åü„ÅãÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
			if (receipt.status === 1) {
				console.log(
					"Domain minted! https://mumbai.polygonscan.com/tx/" + tx.hash
				);

				// domain„ÅÆrecord„Çí„Çª„ÉÉ„Éà„Åó„Åæ„Åô„ÄÇ
				tx = await contract.setRecord(domain, record);
				await tx.wait();

				console.log("Record set! https://mumbai.polygonscan.com/tx/" + tx.hash);

			   // fetchMintsÈñ¢Êï∞ÂÆüË°åÂæå2ÁßíÂæÖ„Å°„Åæ„Åô„ÄÇ
			   setTimeout(() => {
				fetchMints();
			   }, 2000);

				setRecord('');
				setDomain('');
			} else {
				alert("Transaction failed! Please try again");
			}
		}
	} catch (error) {
	  console.log(error);
	}
};


const fetchMints = async () => {
	try {
		const {ethereum} = window;
		if (ethereum) {
			const provider = new ethers.providers.Web3Provider(ethereum);
			const signer = provider.getSigner();
			const contract = new ethers.Contract(CONTRACT_ADDRESS, contractAbi.abi, signer);

			// „Åô„Åπ„Å¶„ÅÆ„Éâ„É°„Ç§„É≥„ÇíÂèñÂæó„Åó„Åæ„Åô„ÄÇ
			const names = await contract.getAllNames();

			// „Éç„Éº„É†„Åî„Å®„Å´„É¨„Ç≥„Éº„Éâ„ÇíÂèñÂæó„Åó„Åæ„Åô„ÄÇ„Éû„ÉÉ„Éî„É≥„Ç∞„ÅÆÂØæÂøú„ÇíÁêÜËß£„Åó„Åæ„Åó„Çá„ÅÜ„ÄÇ
			const mintRecords = await Promise.all(names.map(async (name) => {
			const mintRecord = await contract.records(name);
			const owner = await contract.domains(name);
			return {
				id: names.indexOf(name),
				name: name,
				record: mintRecord,
				owner: owner,
			};
			}));

			console.log("MINTS FETCHED", mintRecords);
			setMints(mintRecords);
		}
	} catch(error) {
	  console.log(error);
	}
}



   const updateDomain = async () => {
	if (!record || !domain) { return }
	setLoading(true);
	console.log("Updating domain", domain, "with record", record);
		try {
			const { ethereum } = window;
			if (ethereum) {
				const provider = new ethers.providers.Web3Provider(ethereum);
				const signer = provider.getSigner();
				const contract = new ethers.Contract(CONTRACT_ADDRESS, contractAbi.abi, signer);

				let tx = await contract.setRecord(domain, record);
				await tx.wait();
				console.log("Record set https://mumbai.polygonscan.com/tx/"+tx.hash);

				fetchMints();
				setRecord('');
				setDomain('');
			}
		} catch(error) {
		  console.log(error);
		}
	   setLoading(false);
    }

   // „Åæ„Å†„Ç¶„Ç©„É¨„ÉÉ„Éà„Å´Êé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÅÆ„É¨„É≥„ÉÄ„É™„É≥„Ç∞„Åß„Åô„ÄÇ
   const renderNotConnectedContainer = () => (

	<div className = "connect-wallet-container">
		<img
		  src = "https://media.giphy.com/media/yy6hXyy2DsM5W/giphy-downsized-large.gif"
		  //src = "https://media.giphy.com/media/1msxZUIsqN5pide6o5/giphy.gif" 
		  //src = "https://media.giphy.com/media/iDJQRjTCenF7A4BRyU/giphy.gif"
		  //src = "https://media.giphy.com/media/3ohhwytHcusSCXXOUg/giphy.gif"
		  alt = "Ninja gif"
		/>
		{/* Connect Wallet „Éú„Çø„É≥„ÅåÊäº„Åï„Çå„Åü„Å®„Åç„ÅÆ„Åø connectWalletÈñ¢Êï∞ „ÇíÂëº„Å≥Âá∫„Åó„Åæ„Åô„ÄÇ */}
		{/* <button className = "cta-button connect-wallet-button"> */}

		<button 
			onClick={connectWallet}
			className = "cta-button connect-wallet-button"
		>
			 Connect Wallet <p>üîúClutch Switch Engage</p>
		</button>
	</div>
   );

   // „Éâ„É°„Ç§„É≥„Éç„Éº„É†„Å®„Éá„Éº„Çø„ÅÆÂÖ•Âäõ„Éï„Ç©„Éº„É†„Åß„Åô„ÄÇ
   const renderInputForm = () => {

	//Polygon Mumbai Testnet‰∏ä„Å´„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÄÅswitch„Éú„Çø„É≥„Çí„É¨„É≥„ÉÄ„É™„É≥„Ç∞„Åó„Åæ„Åô„ÄÇ
	if (network !== 'Polygon Mumbai Testnet') {
		return (
			<div className="connect-wallet-container">
				{/* <p>Please connect to the Polygon Mumbai Testnet</p> */}
				<p>Please switch to Polygon Mumbai Testnet</p>
				{/* ‰ªä„Éú„Çø„É≥„Åß switchNetwork Èñ¢Êï∞„ÇíÂëº„Å≥Âá∫„Åó„Åæ„Åô„ÄÇ */}
				<button className='cta-button mint-button' onClick={switchNetwork}>Click here to switch</button>
			</div>
		);
	} //else {
	return (
		<div className="form-container">
			<div className="first-row">
				<input
				  type="text"
				  value={domain}
				  placeholder='domain'
				  onChange={(e) => setDomain(e.target.value)}
				/>
				<p className='tld'> {tld} </p>
			</div>

			<input
				type="text"
				value={record}
				placeholder='whats ur horsepower?'
				onChange={(e) => setRecord(e.target.value)}
			/>
			{/* editing Â§âÊï∞„Åå true „ÅÆÂ†¥Âêà„ÄÅ"Set record" „Å® "Cancel" „Éú„Çø„É≥„ÇíË°®Á§∫„Åó„Åæ„Åô„ÄÇ */}
			{ editing ? (
				<div className="button-container">
					{/* updateDomainÈñ¢Êï∞„ÇíÂëº„Å≥Âá∫„Åó„Åæ„Åô„ÄÇ */}
					<button className='cta-button mint-button' disabled={loading} onClick={updateDomain}>
						Set record
					</button>
					 {/* editing „Çí false „Å´„Åó„Å¶Edit„É¢„Éº„Éâ„Åã„ÇâÊäú„Åë„Åæ„Åô„ÄÇ*/}
					 <button className='cta-button mint-button' onClick={() => {setEditing(false)}}>
						Cancel
					 </button>
				</div>
			) : (
				// editing Â§âÊï∞„Åå true „Åß„Å™„ÅÑÂ†¥Âêà„ÄÅMint „Éú„Çø„É≥„Åå‰ª£„Çè„Çä„Å´Ë°®Á§∫„Åï„Çå„Åæ„Åô„ÄÇ
				<button className='cta-button mint-button' disabled={loading} onClick={mintDomain}>
					Mint
				</button>
